using System.Text.Json;
using System.Text.Json.Serialization;
using PayabliApi.Core;

namespace PayabliApi;

[Serializable]
public record NotificationReportRequestContent : IJsonOnDeserialized
{
    [JsonExtensionData]
    private readonly IDictionary<string, JsonElement> _extensionData =
        new Dictionary<string, JsonElement>();

    /// <summary>
    /// The notification's event name.
    /// </summary>
    [JsonPropertyName("eventType")]
    public string? EventType { get; set; }

    /// <summary>
    /// Indicate the format of report file to be generated by the engine.
    /// </summary>
    [JsonPropertyName("fileFormat")]
    public NotificationReportRequestContentFileFormat? FileFormat { get; set; }

    /// <summary>
    /// Array of pairs key:value to insert in request body to target in **method** = *report-web*.
    /// </summary>
    [JsonPropertyName("internalData")]
    public IEnumerable<KeyValueDuo>? InternalData { get; set; }

    /// <summary>
    /// The kind report to generate.
    /// </summary>
    [JsonPropertyName("reportName")]
    public NotificationReportRequestContentReportName? ReportName { get; set; }

    [JsonPropertyName("timeZone")]
    public int? TimeZone { get; set; }

    /// <summary>
    /// Used internally to reference the entity or object generating the event.
    /// </summary>
    [JsonPropertyName("transactionId")]
    public string? TransactionId { get; set; }

    /// <summary>
    /// Array of pairs key:value to insert in header of request to target in **method** = *report-web*.
    /// </summary>
    [JsonPropertyName("webHeaderParameters")]
    public IEnumerable<KeyValueDuo>? WebHeaderParameters { get; set; }

    [JsonIgnore]
    public ReadOnlyAdditionalProperties AdditionalProperties { get; private set; } = new();

    void IJsonOnDeserialized.OnDeserialized() =>
        AdditionalProperties.CopyFromExtensionData(_extensionData);

    /// <inheritdoc />
    public override string ToString()
    {
        return JsonUtils.Serialize(this);
    }
}
